var redis = require('redis').createClient()
  , shorten = require('shorten')(redis)
  , async = require('async')
  , db = require('./config').db
  ;

db.bind('userDAL');

function log(err) {
  if(err) {
    console.log(err.stack);
  }
}

function createUser(user, callback) {

  async.waterfall([
      function(fn) {
        shorten.nextId('userDAL', fn);
      }
    , function(uid, fn) {
        user.uid = uid;
        db.user.save(user, fn);
      }
  ], callback);

}

for(var i = 0; i<10; i++) {
  createUser({name: 'userDAL' + i}, log);
}
